// For JPG images
declare module '*.jpg' {
    const src: string;
    export default src;
  }
  
  // For PNG images
  declare module '*.png' {
    const src: string;
    export default src;
  }
  
  // For SVG images
  declare module '*.svg' {
    const src: string;
    export default src;
  }
  
  // For JPEG images
  declare module '*.jpeg' {
    const src: string;
    export default src;
  }